import React, { useEffect, useState } from 'react'
import fetchNEOs from './APICalls';
import { Route } from 'react-router'
import Form from './Components/Form/Form';
import NEOsArea from './Components/NEOsArea/NEOsArea';


const App = () => {

  const[NEOs, setNEOs] = useState(null)
  const[startDate, setStartDate] = useState('')
  const[endDate, setEndDate] = useState('')
  const[userSubmit, setUserSubmit] = useState(false)
  const[errorMessage, setErrorMessage] = useState('')


  useEffect(() => {determineDates()}, [])
  useEffect(() => {logShit()}, [startDate, endDate])
  useEffect(() => {
    fetchUserNEOsByDate()
  }, [userSubmit])

  const logShit = () => {
    console.log(startDate, endDate)
  }

  const determineDates = () => {
    const current = new Date()
    const today = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`
    const week = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate() + 7}`
    setStartDate(today) 
    setEndDate(week)

  }

  const fetchUserNEOsByDate = () => {
    fetchNEOs(startDate, endDate).then((res) => {
      if (!res.ok) {
        setErrorMessage('Oops, something went wrong.  Please try again later.')
      } else {
        return res.json()
      }
    })
    .then((NEOData) => {
      console.log(NEOData)
      console.log('NEOData: ', NEOData.near_earth_objects)
      setNEOs(NEOData.near_earth_objects)
    })

    // setNEOs(rawResponse)
  }



  return (
    <div className='App'>
      <header>
        <h1>TITLE!!!</h1>
        <Form setStartDate={setStartDate} setEndDate={setEndDate} setUserSubmit={setUserSubmit} />
      </header>
      <main>
        {console.log('NEOs: ', NEOs)}
        {NEOs && <NEOsArea NEOs={NEOs} />}
      </main>
    </div>
  )
}

export default App;
